package _1;/** * Created by 28013 on 2016/9/5 0005. */public class sort {	public static void main(String[] args){		int[] array = new int[10];		for (int i = 0; i < 10; i++)			array[i] = (int)(Math.random()*100);		for(int i = 0; i < array.length - 1; i++)			for(int j = 0; j < array.length - i - 1; ++j)				if(array[j] > array[j + 1]){					int t = array[j];					array[j] = array[j + 1];					array[j + 1] = t;				}		for (int i:array			 ) {			System.out.println(String.format("%d ", i));		}	}}